'          \tbreak;',
'      }',
'   }',
'   else {',
'      switch (type) {',
'         case "temp":',
'            dir = cordova.file.externalCacheDirectory || cordova.file.cacheDirectory;',
'            break;',
'         case "private":',
'            dir = cordova.file.externalDataDirectory || cordova.file.dataDirectory;',
'            break;',
'         case "saved":',
