'{UXPRERENDEREDALIAS}_DlgObj.phoneGapListFilesRecurseURL = function(rootpath, onCompletion, onError) {',
'   if (!{UXPRERENDEREDALIAS}_DlgObj.phoneGapFilesAvailable()) {',
